import {useState, FormEvent} from "react";
import {Article} from "../types/Article";

interface ArticleFormProps {
  onAdd: (article: Omit<Article, "id">) => void;
}

const ArticleForm: React.FC<ArticleFormProps> = ({onAdd}) => {
  // État local : nom de l'article
  const [name, setName] = useState("");

  // État local : quantité (par défaut 1)
  const [quantity, setQuantity] = useState(0);

  // État local : prix unitaire
  const [unitPrice, setUnitPrice] = useState(0);

  /**
   * Gestion de la soumission du formulaire
   * Crée un objet article et le transmet au parent
   */
  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();

    // Validation : le nom ne doit pas être vide
    if (!name.trim()) return;

    // Envoi de l'article au parent (App.tsx)
    onAdd({
      nom: name,
      quantite: quantity,
      prixUnitaire: unitPrice,
    });

    // Réinitialisation du formulaire
    setName("");
    setQuantity(1);
    setUnitPrice(0);
  };

  return (
    <form onSubmit={handleSubmit} className="article-form">
      {/* Champ : nom de l'article */}
      <input
        type="text"
        placeholder="Nom de l'article"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
      />

      {/* Champ : quantité */}
      <input
        type="number"
        placeholder="Quantité"
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
        min="1"
        required
      />

      {/* Champ : prix unitaire */}
      <input
        type="number"
        placeholder="Prix unitaire (€)"
        value={unitPrice}
        onChange={(e) => setUnitPrice(Number(e.target.value))}
        min="0"
        step="0.01"
        required
      />

      {/* Bouton de soumission */}
      <button type="submit">Ajouter</button>
    </form>
  );
};

export default ArticleForm;
