import {useState, FormEvent} from "react";

interface ProjectModalProps {
  onConfirm: (projectName: string) => void;
  onCancel: () => void;
}

const ProjectModal: React.FC<ProjectModalProps> = ({onConfirm, onCancel}) => {
  // Ã‰tat local : nom du projet Ã  crÃ©er
  const [projectName, setProjectName] = useState("");

  /**
   * Gestion de la soumission du formulaire
   * Valide et transmet le nom au parent
   */
  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();

    // Validation : le nom ne doit pas Ãªtre vide
    const trimmedName = projectName.trim();
    if (!trimmedName) return;

    // Envoi du nom au parent (App.tsx)
    onConfirm(trimmedName);
  };

  return (
    <div className="modal-overlay" onClick={onCancel}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>ðŸ“‹ Nouveau projet</h2>

        <form onSubmit={handleSubmit} className="modal-form">
          {/* Champ de saisie du nom du projet */}
          <input
            type="text"
            placeholder="Nom du projet"
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            autoFocus
            required
          />

          {/* Boutons d'action */}
          <div className="modal-buttons">
            <button type="submit" className="btn-confirm">
              CrÃ©er
            </button>
            <button type="button" className="btn-cancel" onClick={onCancel}>
              Annuler
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectModal;
