import {Article} from "../types/Article";

interface ArticleTableProps {
  articles: Article[];
  onDelete: (id: number) => void;
  onUpdateQuantity: (id: number, delta: number) => void;
}

const ArticleTable: React.FC<ArticleTableProps> = ({
  articles,
  onDelete,
  onUpdateQuantity,
}) => {
  /**
   * Calcule le prix total d'un article (quantit√© √ó prix unitaire)
   */
  const calculateLineTotal = (article: Article): number => {
    return article.quantite * article.prixUnitaire;
  };

  /**
   * Calcule le prix total de tous les articles
   */
  const calculateGrandTotal = (): number => {
    return articles.reduce(
      (sum, article) => sum + calculateLineTotal(article),
      0,
    );
  };

  // Affichage si aucun article n'existe
  if (articles.length === 0) {
    return (
      <p className="empty-state">Aucun article. Ajoutez-en un ci-dessus.</p>
    );
  }

  return (
    <div className="article-table-container">
      <table className="article-table">
        <thead>
          <tr>
            <th>D√©signation</th>
            <th>Quantit√©</th>
            <th>Prix unitaire</th>
            <th>Total</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {articles.map((article) => (
            <tr key={article.id}>
              {/* Nom de l'article */}
              <td>{article.nom}</td>

              {/* Contr√¥le de quantit√© avec boutons +/- */}
              <td className="quantite-cell">
                {/* Bouton diminuer */}
                <button
                  className="btn-quantite"
                  onClick={() => onUpdateQuantity(article.id, -1)}
                  title="Diminuer la quantit√©"
                >
                  ‚àí
                </button>

                {/* Affichage de la quantit√© actuelle */}
                <span className="quantite-value">{article.quantite}</span>

                {/* Bouton augmenter */}
                <button
                  className="btn-quantite"
                  onClick={() => onUpdateQuantity(article.id, 1)}
                  title="Augmenter la quantit√©"
                >
                  +
                </button>
              </td>

              {/* Prix unitaire avec 2 d√©cimales */}
              <td className="prix">{article.prixUnitaire.toFixed(2)} ‚Ç¨</td>

              {/* Prix total de la ligne */}
              <td className="prix total-ligne">
                {calculateLineTotal(article).toFixed(2)} ‚Ç¨
              </td>

              {/* Bouton de suppression */}
              <td>
                <button
                  className="btn-supprimer"
                  onClick={() => onDelete(article.id)}
                  title="Supprimer l'article"
                >
                  üóëÔ∏è
                </button>
              </td>
            </tr>
          ))}
        </tbody>

        {/* Ligne de total g√©n√©ral */}
        <tfoot>
          <tr>
            <td colSpan={3} className="total-label">
              <strong>Total g√©n√©ral</strong>
            </td>
            <td className="prix total-general">
              <strong>{calculateGrandTotal().toFixed(2)} ‚Ç¨</strong>
            </td>
            <td></td>
          </tr>
        </tfoot>
      </table>
    </div>
  );
};

export default ArticleTable;
