import {useState} from "react";
import "./App.css";
import ArticleForm from "./components/ArticleForm";
import ArticleTable from "./components/ArticleTable";
import {Article} from "./types/Article";
import {Projet} from "./types/Projet";

function App() {
  const [projets, setProjets] = useState<Projet[]>([
    {id: 1, nom: "Projet exemple", articles: []},
  ]);
  const [projetActif, setProjetActif] = useState<number>(1);

  const projet = projets.find((p) => p.id === projetActif);

  const ajouterArticle = (article: Omit<Article, "id">) => {
    setProjets((prev) =>
      prev.map((p) =>
        p.id === projetActif
          ? {
              ...p,
              articles: [...p.articles, {...article, id: Date.now()}],
            }
          : p,
      ),
    );
  };

  const supprimerArticle = (id: number) => {
    setProjets((prev) =>
      prev.map((p) =>
        p.id === projetActif
          ? {
              ...p,
              articles: p.articles.filter((a) => a.id !== id),
            }
          : p,
      ),
    );
  };

  const modifierQuantite = (id: number, delta: number) => {
    setProjets((prev) =>
      prev.map((p) =>
        p.id === projetActif
          ? {
              ...p,
              articles: p.articles
                .map((a) =>
                  a.id === id
                    ? {...a, quantite: Math.max(0, a.quantite + delta)}
                    : a,
                )
                .filter((a) => a.quantite > 0),
            }
          : p,
      ),
    );
  };

  const ajouterProjet = () => {
    const nouveauId = Math.max(...projets.map((p) => p.id)) + 1;
    setProjets([
      ...projets,
      {id: nouveauId, nom: `Projet ${nouveauId}`, articles: []},
    ]);
    setProjetActif(nouveauId);
  };

  const calculerTotal = (articles: Article[]) => {
    return articles.reduce((sum, a) => sum + a.prixUnitaire * a.quantite, 0);
  };

  return (
    <div className="app">
      <header>
        <h1>ðŸ“‹ Gestion de Devis</h1>
      </header>

      {/* GRILLE DE PROJETS */}
      <div className="projets-grid">
        {projets.map((p) => (
          <div
            key={p.id}
            className={`projet-card ${p.id === projetActif ? "actif" : ""}`}
            onClick={() => setProjetActif(p.id)}
          >
            <h3>{p.nom}</h3>
            <p className="prix-total">
              {calculerTotal(p.articles).toFixed(2)} â‚¬
            </p>
            <span className="nb-articles">
              {p.articles.length} article{p.articles.length > 1 ? "s" : ""}
            </span>
          </div>
        ))}

        <button className="projet-card nouveau" onClick={ajouterProjet}>
          <span className="plus">+</span>
          <span>Nouveau projet</span>
        </button>
      </div>

      {/* TABLEAU DU PROJET ACTIF */}
      {projet && (
        <div className="projet-actif">
          <h2>{projet.nom}</h2>
          <ArticleForm onAdd={ajouterArticle} />
          <ArticleTable
            articles={projet.articles}
            onDelete={supprimerArticle}
            onUpdateQuantite={modifierQuantite}
          />
        </div>
      )}
    </div>
  );
}

export default App;
